import { StyleSheet, Text, View, Image, TouchableOpacity } from 'react-native'
import React from 'react'
import Waters from '../components/Waters';
import { allWaters } from '../components/Data';
import { profilePic, profileName } from '../components/Settings';

const Details = () => {
  return (
    <View style={styles.container}>
        <View style={[styles.section, {flex: 2}]}>
            <View>
                <View style={{alignSelf: 'center', flexDirection: 'row', alignItems: 'center'}}>
                    <Text style={{fontSize: 25, fontWeight: 'bold', color: 'steelblue'}}>{profileName}</Text>
                    <Image
                        style={{width: 20, height: 20, marginLeft: 10}}
                        source={require('../assets/images/edit8.png')}
                    />
                </View>
            </View>
                <Image
                    style={{width: 170, height: 170, borderRadius: 150, alignSelf: 'center', marginTop: 25}}
                    source={ profilePic }
                />
                <TouchableOpacity style={{alignSelf: 'center', backgroundColor: '#DEDEDE', padding: 10, marginTop: 10, borderRadius: 8, elevation: 2}}><Text style={{color: 'red'}}>Αλλαγή</Text></TouchableOpacity>
        </View>
        <View style={styles.section}>
            <View style={{flexDirection: 'row'}}>
                <Image
                    style={styles.sectionImg}
                    source={require('../assets/images/i-water2.png')}
                />
                <Text style={styles.sectionTitle}>Πομόνες ({allWaters.length})</Text>
                <TouchableOpacity style={{marginLeft: 'auto', elevation: 2}}>
                    <Image
                        style={styles.sectionImg}
                        source={require('../assets/images/i-plus2.png')}
                    />
                </TouchableOpacity>
            </View>
            <View style={{flexDirection: 'row', marginTop: 15}}>
                <Waters />
            </View>
        </View>
        <View style={styles.section}>
            <View style={{flexDirection: 'row'}}>
                <Image
                    style={styles.sectionImg}
                    source={require('../assets/images/i-settings2.png')}
                />
                <Text style={styles.sectionTitle}>Ρυθμίσεις</Text>
            </View>
        </View>
    </View>
  )
}

export default Details

const styles = StyleSheet.create({
    container: {
        minHeight: '100%',
        backgroundColor: '#EEEEEE',
        padding: 20
    },
    section: {
        flex: 1,
        marginVertical: 12,
        padding: 5,
    },
    sectionImg: {
        width: 32,
        height: 32,
        marginRight: 15
    },
    sectionTitle: {
        fontSize: 18,
        fontWeight: 'bold',
        letterSpacing: 2,
        paddingHorizontal: 5
    }
})
