import { Image, Pressable, StyleSheet, Text, View } from 'react-native'
import React, { useCallback, useEffect, useMemo, useRef } from 'react'
import { GestureHandlerRootView } from 'react-native-gesture-handler';
import BottomSheet, { BottomSheetBackdrop, useBottomSheet } from '@gorhom/bottom-sheet';
import * as Animatable from 'react-native-animatable';

const BottomCard = React.forwardRef((props, ref) => {
    const snapPoints = useMemo(() => ['30%', '65%'], []);
    


    const doStuff = () => {
      ref.current.snapToIndex(1)
    }

      useEffect(()=>{
        console.log('props: ' + props.data.name)
        console.log('ref: ' + ref)
      }, [])

    // renders
    const renderBackdrop = useCallback(
      props => (
        <BottomSheetBackdrop
          {...props}
          disappearsOnIndex={-1}
          appearsOnIndex={0}
        />
      ), []);

  return (
    <BottomSheet
          ref={ref}
         animateOnMount= {false}
         index={-1}
         children
         snapPoints={snapPoints}
         backdropComponent={renderBackdrop}
         //enablePanDownToClose
    >
        <Animatable.View
             style={styles.card}
             animation= "fadeInUp"
             easing= "ease-in-out"
             duration= {300}
        >
          <View style={{backgroundColor: 'white', paddingHorizontal: 10, flexDirection: 'row', alignItems: 'center', marginBottom: 20}}>
            <Text style={{fontSize: 25, fontWeight: 'bold', marginRight: 10}}>Όνομα Χωραφιού</Text>
            <View style={{width: 13, height: 13, borderRadius: 100, backgroundColor: '#BADA55'}}></View>
          </View>
          <View style={{flexDirection: 'row', justifyContent: 'center', marginBottom: 15, marginTop: 15, paddingHorizontal: 10}}>
            <Pressable onPress={()=>{console.log('START')}} style={styles.control}><Text style={{color: 'green', fontWeight: 'bold'}}>Έναρξη</Text></Pressable>
            <Pressable onPress={()=>{console.log('STOP')}} style={[styles.control, {backgroundColor: '#90002220', color: 'red', marginLeft: 15}]}><Text style={{color: 'red', fontWeight: 'bold'}}>Παύση</Text></Pressable>
          </View>
          <Pressable onPress={doStuff} style={[styles.control, {flexDirection: 'row', alignItems: 'center', justifyContent: 'center', backgroundColor: '#0099FF88', alignSelf: 'stretch', flex:0, marginBottom: 40, height: 65, marginHorizontal: 10}]}>
            <Image source={require('../assets/images/icons/091-clock.png')} style={{width: 32, height: 32, marginRight: 15}}>
            </Image>
            <Text>Προγραμματισμός</Text>
          </Pressable>
          <View style={{flex: 1, borderTopColor: '#EFEFEF', borderTopWidth: 2, padding: 15}}><Text style={{fontWeight: 'bold'}}>Προγραμματίστε πότε θέλετε να σταματήσει/ξεκινήσει το πότισμα:</Text></View>
            <Pressable style={styles.btn} onLongPress={() => {console.info('ΠΡΟΓΡΑΜΜΑΤΙΣΤΗΚΕ ΧΩΡΑΦΙ')}}><Text style={{color: 'white', padding: 15}}>Επιβεβαίωση</Text><Image style={{width: 20, height: 20, marginRight: 10, transform: [{rotate: '180deg'}]}} source={require('../assets/images/icons/i-left.png')} /></Pressable>
        </Animatable.View>
    </BottomSheet>
  )
})

export default BottomCard

const styles = StyleSheet.create({
  card: {
    flex: 1,
    backgroundColor: 'white', 
    paddingBottom: 20
  },
  control: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    height: 80,
    padding: 20,
    backgroundColor: '#BADA5550',
    fontWeight: 'bold',
    alignSelf: 'flex-start',
    borderRadius: 5,
  },
  btn: {
    marginHorizontal: 15,
    flexDirection: 'row',
    alignItems: 'center',
    borderRadius: 8,
    backgroundColor: '#4F9F8FF0'
  }
})
